import React from 'react';
import { 
  Typography, 
  Box, 
  Container, 
  Button, 
  Chip,
  Grid,
  Paper,
  ThemeProvider,
  createTheme
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';

const theme = createTheme({
  typography: {
    fontFamily: 'Arial, sans-serif',
  },
});

const Home: React.FC = () => {
  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md">
        <Box sx={{ my: 4 }}>
          <Typography variant="h2" component="h1" gutterBottom>
            Alan Xue
          </Typography>
          <Typography variant="h4" component="h2" color="primary" gutterBottom>
            Software Engineer
          </Typography>

          <Paper elevation={3} sx={{ p: 3, my: 3 }}>
            <Typography variant="body1" paragraph>
              Skilled Frontend Engineer specializing in React.js. Demonstrated expertise in JavaScript,
              HTML, CSS, and Redux, aligning with Help Scout's technology stack. Proven ability in agile
              environments, optimizing UI/UX for customer-centric solutions, and embracing remote
              collaboration. Eager to contribute to Help Scout's mission by delivering high-quality, impactful
              user experiences.
            </Typography>
          </Paper>

          <Typography variant="h5" gutterBottom sx={{ mt: 4 }}>
            Core Skills
          </Typography>
          <Grid container spacing={1} sx={{ mb: 4 }}>
            {['React.js', 'JavaScript', 'HTML/CSS', 'Redux', 'UI/UX Optimization'].map((skill) => (
              <Grid item key={skill}>
                <Chip label={skill} color="primary" variant="outlined" />
              </Grid>
            ))}
          </Grid>

          <Button 
            variant="contained" 
            color="primary" 
            startIcon={<DownloadIcon />}
            href="/src/resume/Yifan_Alan_Xue.pdf" 
            download
          >
            Download Resume
          </Button>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default Home;
