import React from 'react';
import { Link } from 'react-router-dom';

const headerStyle: React.CSSProperties = {
  backgroundColor: '#4CAF50', // 保持绿色主题
  padding: '10px 0',
};

const navStyle: React.CSSProperties = {
  display: 'flex',
  justifyContent: 'center',
};

const ulStyle: React.CSSProperties = {
  listStyleType: 'none',
  margin: 0,
  padding: 0,
  display: 'flex',
  flexWrap: 'wrap', // 允许在小屏幕上换行
};

const liStyle: React.CSSProperties = {
  margin: '0 10px',
};

const linkStyle: React.CSSProperties = {
  color: 'white',
  textDecoration: 'none',
  padding: '5px 10px',
  borderRadius: '3px',
  transition: 'background-color 0.3s',
};

const Header: React.FC = () => {
  return (
    <header style={headerStyle}>
      <nav style={navStyle}>
        <ul style={ulStyle}>
          <li style={liStyle}><Link to="/" style={linkStyle}>Home</Link></li>
          <li style={liStyle}><Link to="/experience" style={linkStyle}>Experience</Link></li>
          <li style={liStyle}><Link to="/projects" style={linkStyle}>Projects</Link></li>
          <li style={liStyle}><Link to="/skills" style={linkStyle}>Skills</Link></li>
          <li style={liStyle}><Link to="/ai-chat" style={linkStyle}>AI Chat</Link></li>
          <li style={liStyle}><Link to="/guestbook" style={linkStyle}>Guestbook</Link></li>
          <li style={liStyle}><Link to="/contact" style={linkStyle}>Contact</Link></li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
