import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  ThemeProvider,
  createTheme,
  Container,
  Fade
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ChatIcon from '@mui/icons-material/Chat';

const theme = createTheme({
  typography: {
    fontFamily: 'Arial, sans-serif',
  },
  palette: {
    primary: {
      main: '#4CAF50',
    },
  },
});

const AIChat: React.FC = () => {
  const [messages, setMessages] = useState<string[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputMessage(e.target.value);
  };

  const simulateAIResponse = (question: string): string => {
    const responses = [
      "That's an interesting question. Let me think about it.",
      "I'm not sure about that, but here's what I know...",
      "Based on my knowledge, I would say...",
      "That's a complex topic. Here's a simplified explanation:",
      "I don't have a definitive answer, but we could explore this further."
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages(prevMessages => [...prevMessages, `You: ${inputMessage}`]);
      const aiResponse = simulateAIResponse(inputMessage);
      setTimeout(() => {
        setMessages(prevMessages => [...prevMessages, `AI: ${aiResponse}`]);
      }, 1000); // Simulate AI thinking time
      setInputMessage('');
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md">
        <Box sx={{ padding: 4 }}>
          <Typography 
            variant="h4" 
            gutterBottom 
            color="primary" 
            sx={{ 
              borderBottom: '2px solid #4CAF50',
              paddingBottom: 1,
              marginBottom: 4,
              display: 'inline-block'
            }}
          >
            <ChatIcon sx={{ verticalAlign: 'middle', marginRight: 1 }} />
            AI Chat
          </Typography>
          <Fade in={true} timeout={1000}>
            <Paper
              elevation={3}
              sx={{
                height: '60vh',
                display: 'flex',
                flexDirection: 'column',
                mb: 2,
                p: 2,
                transition: 'all 0.3s',
                '&:hover': {
                  transform: 'scale(1.01)',
                  boxShadow: 6,
                },
              }}
            >
              <List sx={{ flex: 1, overflowY: 'auto', mb: 2 }}>
                {messages.map((message, index) => (
                  <ListItem key={index}>
                    <ListItemText primary={message} />
                  </ListItem>
                ))}
                <div ref={messagesEndRef} />
              </List>
              <Box sx={{ display: 'flex' }}>
                <TextField
                  fullWidth
                  variant="outlined"
                  value={inputMessage}
                  onChange={handleInputChange}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message..."
                  sx={{ mr: 1 }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  endIcon={<SendIcon />}
                  onClick={handleSendMessage}
                >
                  Send
                </Button>
              </Box>
            </Paper>
          </Fade>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default AIChat;
