import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  ThemeProvider,
  createTheme,
  Container,
  Fade
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ChatIcon from '@mui/icons-material/Chat';

// 这里应该是你的简历内容，你需要手动提取关键信息并格式化
const resumeContent = {
  name: "Alan Xue",
  currentPosition: "Software Developer at Sagent Lending",
  skills: ["React", "JavaScript", "HTML", "CSS", "Redux", "Tailwind CSS", "MUI"],
  experience: [
    {
      company: "Sagent Lending",
      position: "Software Developer",
      period: "03/2023 to Current",
      achievements: [
        "Developed secure mode, tag modal, and topic references",
        "Engineered a new web page for localized asset management",
        "Transitioned MUI-based components to custom Tailwind-based components"
      ]
    },
    {
      company: "Walmart",
      position: "Front-End Software Developer",
      period: "01/2022 to 01/2023",
      achievements: [
        "Developed reusable components using React.js and HTML",
        "Designed responsive layouts and animations with CSS Media Queries",
        "Played a key role in the entire Software Development Life Cycle"
      ]
    }
  ],
  education: [
    {
      degree: "Master of Science",
      school: "Tulane University",
      location: "New Orleans, LA",
      period: "2020-2021"
    },
    {
      degree: "Bachelor of Science",
      school: "Michigan State University",
      location: "East Lansing, MI",
      period: "2015-2019"
    }
  ]
};

const theme = createTheme({
  typography: {
    fontFamily: 'Arial, sans-serif',
  },
  palette: {
    primary: {
      main: '#4CAF50',
    },
  },
});

const AIChat: React.FC = () => {
  const [messages, setMessages] = useState<string[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputMessage(e.target.value);
  };

  const simulateAIResponse = (question: string): string => {
    question = question.toLowerCase();
    if (question.includes("name")) {
      return `My name is ${resumeContent.name}.`;
    } else if (question.includes("current job") || question.includes("current position")) {
      return `I am currently working as a ${resumeContent.currentPosition}.`;
    } else if (question.includes("skills")) {
      return `My skills include: ${resumeContent.skills.join(", ")}.`;
    } else if (question.includes("experience")) {
      const latestJob = resumeContent.experience[0];
      return `My most recent experience is as a ${latestJob.position} at ${latestJob.company} from ${latestJob.period}.`;
    } else if (question.includes("education")) {
      const latestEducation = resumeContent.education[0];
      return `I have a ${latestEducation.degree} from ${latestEducation.school}, which I completed in ${latestEducation.period}.`;
    } else {
      return "I'm sorry, I don't have specific information about that. Could you please ask about my name, current job, skills, experience, or education?";
    }
  };

  const handleSendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages(prevMessages => [...prevMessages, `You: ${inputMessage}`]);
      const aiResponse = simulateAIResponse(inputMessage);
      setTimeout(() => {
        setMessages(prevMessages => [...prevMessages, `AI: ${aiResponse}`]);
      }, 1000); // Simulate AI thinking time
      setInputMessage('');
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md">
        <Box sx={{ padding: 4 }}>
          <Typography 
            variant="h4" 
            gutterBottom 
            color="primary" 
            sx={{ 
              borderBottom: '2px solid #4CAF50',
              paddingBottom: 1,
              marginBottom: 4,
              display: 'inline-block'
            }}
          >
            <ChatIcon sx={{ verticalAlign: 'middle', marginRight: 1 }} />
            AI Chat
          </Typography>
          <Fade in={true} timeout={1000}>
            <Paper
              elevation={3}
              sx={{
                height: '60vh',
                display: 'flex',
                flexDirection: 'column',
                mb: 2,
                p: 2,
                transition: 'all 0.3s',
                '&:hover': {
                  transform: 'scale(1.01)',
                  boxShadow: 6,
                },
              }}
            >
              <List sx={{ flex: 1, overflowY: 'auto', mb: 2 }}>
                {messages.map((message, index) => (
                  <ListItem key={index}>
                    <ListItemText primary={message} />
                  </ListItem>
                ))}
                <div ref={messagesEndRef} />
              </List>
              <Box sx={{ display: 'flex' }}>
                <TextField
                  fullWidth
                  variant="outlined"
                  value={inputMessage}
                  onChange={handleInputChange}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask about Alan's background..."
                  sx={{ mr: 1 }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  endIcon={<SendIcon />}
                  onClick={handleSendMessage}
                >
                  Send
                </Button>
              </Box>
            </Paper>
          </Fade>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default AIChat;
